from django.conf.urls import urlfrom goods import viewsapp_name = 'goods'urlpatterns = [    url(r'^$', views.IndexView.as_view(), name='home'),    url(r'^category/(\d+)/$', views.IndexView.as_view(), name='category'),    url(r'^category/(\d+)/page/(\d+)/$', views.IndexView.as_view(), name='category_with_page'),    url(r'^goodsdetails/(\d+)/$', views.DetailView.as_view(), name='goodsdetail'),]